with.script:
const title = 'Netlify deploy unhealthy';
const body = 'Automated monitor detected a failed or unhealthy Netlify deploy. Please check the deploys page and logs.';
// Avoid duplicate open issues with same title
const { data: issues } = await github.rest.issues.listForRepo({ owner: context.repo.owner, repo: context.repo.repo, state: 'open' });
if (!issues.find(i => i.title === title)) {
  await github.rest.issues.create({ owner: context.repo.owner, repo: context.repo.repo, title, body });
}

const version = '${{ steps.version.outputs.version }}';
const message = ` **Release ${version} Deployed**\n\nVersion ${version} has been successfully released and deployed to production.`;

// Create a release summary issue
github.rest.issues.create({
  owner: context.repo.owner,
  repo: context.repo.repo,
  title: `Release ${version} Summary`,
  body: message,
  labels: ['release', 'deployment']
});

const invalidCount = '${{ steps.validate.outputs.invalid_count }}';
const message = ` **Workflow Auto-Healer Report**\n\nFound ${invalidCount} invalid workflow files that need manual attention.\n\nPlease review and fix these workflows manually.`;

github.rest.issues.create({
  owner: context.repo.owner,
  repo: context.repo.repo,
  title: `Workflow Auto-Healer: ${invalidCount} Invalid Workflows Found`,
  body: message,
  labels: ['workflow-fix', 'automation', 'needs-attention']
});
